import React, { useState, useMemo, useRef, useCallback } from 'react';
import { Table, Form, Row, Col } from 'react-bootstrap';
import './InfiniteScrollTable.css'; // Import a separate CSS file for styling

const InfiniteScrollTable = ({ data }) => {
  const [visibleItems, setVisibleItems] = useState(10);
  const [hasMore, setHasMore] = useState(true);
  const [selectedRows, setSelectedRows] = useState([]);
  const [sortColumn, setSortColumn] = useState(null);
  const [sortDirection, setSortDirection] = useState('asc');
  const [filterValues, setFilterValues] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  const observer = useRef(
    new IntersectionObserver(
      (entries) => {
        const lastEntry = entries[entries.length - 1];
        if (lastEntry.isIntersecting && hasMore) {
          setVisibleItems((prevVisibleItems) => Math.min(prevVisibleItems + 10, data.length));
        }
      },
      { threshold: 1 }
    )
  );

  const lastRowRef = useCallback(
    (node) => {
      if (node !== null) {
        observer.current.observe(node);
      }
    },
    [observer]
  );

  const handleCheckboxChange = (id) => {
    const updatedSelectedRows = selectedRows.includes(id)
      ? selectedRows.filter((rowId) => rowId !== id)
      : [...selectedRows, id];
    setSelectedRows(updatedSelectedRows);
  };

  const handleSort = (column) => {
    setSortColumn(column);
    setSortDirection((prevDirection) => (prevDirection === 'asc' ? 'desc' : 'asc'));
  };

  const handleFilterChange = (column, value) => {
    setFilterValues((prevValues) => ({ ...prevValues, [column]: value }));
  };

  const handleSearchChange = (value) => {
    setSearchTerm(value);
  };

  const filteredAndSortedData = useMemo(() => {
    let filteredData = data.slice(0, visibleItems).filter((item) =>
      Object.entries(filterValues).every(([col, filterValue]) =>
        filterValue ? String(item[col]).includes(filterValue) : true
      )
    );

    filteredData = filteredData.filter((item) =>
      Object.values(item).some((val) =>
        String(val).toLowerCase().includes(searchTerm.toLowerCase())
      )
    );

    if (sortColumn) {
      filteredData.sort((a, b) => {
        const aValue = a[sortColumn];
        const bValue = b[sortColumn];
        const order = sortDirection === 'asc' ? 1 : -1;

        if (typeof aValue === 'string' && typeof bValue === 'string') {
          return aValue.localeCompare(bValue) * order;
        } else {
          return (aValue - bValue) * order;
        }
      });
    }

    return filteredData;
  }, [data, visibleItems, filterValues, sortColumn, sortDirection, searchTerm]);

  return (
    <div className="infinite-scroll-table-container">
      <Table striped bordered hover>
        <thead className="sticky-top">
          <tr>
            <th>
              <Form.Check
                type="checkbox"
                label=""
                checked={selectedRows.length === visibleItems}
                onChange={() => handleCheckboxChange('selectAll')}
              />
            </th>
            <th onClick={() => handleSort('id')}>ID</th>
            <th onClick={() => handleSort('name')}>Name</th>
            {/* ... other table headers ... */}
          </tr>
        </thead>
        <tbody>
          {filteredAndSortedData.map((item, index) => {
            if (index === visibleItems - 1) {
              return (
                <tr key={item.id} ref={lastRowRef}>
                  <td>
                    <Form.Check
                      type="checkbox"
                      label=""
                      checked={selectedRows.includes(item.id)}
                      onChange={() => handleCheckboxChange(item.id)}
                    />
                  </td>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  {/* ... other table cells ... */}
                </tr>
              );
            } else {
              return (
                <tr key={item.id}>
                  <td>
                    <Form.Check
                      type="checkbox"
                      label=""
                      checked={selectedRows.includes(item.id)}
                      onChange={() => handleCheckboxChange(item.id)}
                    />
                  </td>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  {/* ... other table cells ... */}
                </tr>
              );
            }
          })}
        </tbody>
      </Table>

      {/* Search Bar */}
      <Row className="mt-3">
        <Col>
          <Form.Group controlId="search">
            <Form.Label>Search:</Form.Label>
            <Form.Control
              type="text"
              value={searchTerm}
              onChange={(e) => handleSearchChange(e.target.value)}
            />
          </Form.Group>
        </Col>
      </Row>

      {/* Filter Dropdowns */}
      <Row className="mb-3">
        {Object.keys(data[0]).map((column) => (
          <Col key={column}>
            <Form.Group controlId={`filter-${column}`}>
              <Form.Label>{column}:</Form.Label>
              <Form.Control
                as="select"
                value={filterValues[column] || ''}
                onChange={(e) => handleFilterChange(column, e.target.value)}
              >
                <option value="">All</option>
                {Array.from(new Set(data.map((item) => item[column]))).map((value) => (
                  <option key={value} value={value}>
                    {value}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>
          </Col>
        ))}
      </Row>

      {/* Sort Indicator */}
      <Row className="mb-3">
        <Col>
          <div>
            <span className="mr-2">Sort by:</span>
            {Object.keys(data[0]).map((column) => (
              <span
                key={column}
                className="cursor-pointer"
                onClick={() => handleSort(column)}
              >
                {column}{' '}
                {sortColumn === column && (sortDirection === 'asc' ? '▲' : '▼')}
              </span>
            ))}
          </div>
        </Col>
      </Row>
    </div>
  );
};

export default InfiniteScrollTable;
