import React, { useState, useEffect } from 'react';
import { Table, Pagination } from 'react-bootstrap';

const PaginatedTable = ({ data, itemsPerPage }) => {
  const [selectedRows, setSelectedRows] = useState([]);
  const [sortColumn, setSortColumn] = useState(null);
  const [sortDirection, setSortDirection] = useState('asc');
  const [filterValues, setFilterValues] = useState({});
  const [searchTerm, setSearchTerm] = useState('');

  const handleCheckboxChange = (id) => {
    const updatedSelectedRows = selectedRows.includes(id)
      ? selectedRows.filter((rowId) => rowId !== id)
      : [...selectedRows, id];
    setSelectedRows(updatedSelectedRows);
  };

  const handleSort = (column) => {
    setSortColumn(column);
    setSortDirection((prevDirection) => (prevDirection === 'asc' ? 'desc' : 'asc'));
  };

  const handleFilterChange = (column, value) => {
    setFilterValues((prevValues) => ({ ...prevValues, [column]: value }));
  };

  const handleSearchChange = (value) => {
    setSearchTerm(value);
  };

  const filteredAndSortedData = useMemo(() => {
    let filteredData = data.filter((item) =>
      Object.entries(filterValues).every(([col, filterValue]) =>
        filterValue ? String(item[col]).includes(filterValue) : true
      )
    );

    // Apply searching
    filteredData = filteredData.filter((item) =>
      Object.values(item).some((val) =>
        String(val).toLowerCase().includes(searchTerm.toLowerCase())
      )
    );

    // Apply sorting
    if (sortColumn) {
      filteredData.sort((a, b) => {
        const aValue = a[sortColumn];
        const bValue = b[sortColumn];
        const order = sortDirection === 'asc' ? 1 : -1;

        if (typeof aValue === 'string' && typeof bValue === 'string') {
          return aValue.localeCompare(bValue) * order;
        } else {
          return (aValue - bValue) * order;
        }
      });
    }

    return filteredData;
  }, [data, filterValues, sortColumn, sortDirection, searchTerm]);

  useEffect(() => {
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const slicedItems = data.slice(indexOfFirstItem, indexOfLastItem);

    setCurrentItems(slicedItems);
  }, [data, currentPage, itemsPerPage]);

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
    <Table striped bordered hover>
      <thead>
        <tr>
          <th>
            <input
              type="checkbox"
              checked={selectedRows.length === currentItems.length}
              onChange={() => handleCheckboxChange('selectAll')}
            />
          </th>
          <th onClick={() => handleSort('id')}>ID</th>
          <th onClick={() => handleSort('name')}>Name</th>
          {/* ... other table headers ... */}
        </tr>
      </thead>
      <tbody>
        {currentItems.map((item) => (
          <tr key={item.id}>
            <td>
              <input
                type="checkbox"
                checked={selectedRows.includes(item.id)}
                onChange={() => handleCheckboxChange(item.id)}
              />
            </td>
            <td>{item.id}</td>
            <td>{item.name}</td>
            {/* ... other table cells ... */}
          </tr>
        ))}
      </tbody>
    </Table>

    {/* Search Bar */}
    <div>
      <label>Search:</label>
      <input type="text" value={searchTerm} onChange={(e) => handleSearchChange(e.target.value)} />
    </div>

    {/* Filter Dropdowns */}
    <div>
      {Object.keys(data[0]).map((column) => (
        <div key={column}>
          <label>{column}:</label>
          <select
            value={filterValues[column] || ''}
            onChange={(e) => handleFilterChange(column, e.target.value)}
          >
            <option value="">All</option>
            {Array.from(new Set(data.map((item) => item[column]))).map((value) => (
              <option key={value} value={value}>
                {value}
              </option>
            ))}
          </select>
        </div>
      ))}
    </div>

    {/* Sort Indicator */}
    <div>
      Sort by:{' '}
      {Object.keys(data[0]).map((column) => (
        <span key={column} onClick={() => handleSort(column)}>
          {column}{' '}
          {sortColumn === column && (sortDirection === 'asc' ? '▲' : '▼')}
        </span>
      ))}
    </div>

      <Pagination>
        {Array.from({ length: Math.ceil(data.length / itemsPerPage) }).map((_, index) => (
          <Pagination.Item
            key={index + 1}
            active={index + 1 === currentPage}
            onClick={() => paginate(index + 1)}
          >
            {index + 1}
          </Pagination.Item>
        ))}
      </Pagination>
    </div>
  );
};

export default PaginatedTable;
