import React, { useState, useEffect } from 'react';
import { Table } from 'react-bootstrap';

const InfiniteScrollTable = ({ data }) => {
  const [visibleItems, setVisibleItems] = useState(10); // Initial number of visible rows
  const [hasMore, setHasMore] = useState(true);

  const handleScroll = () => {
    const scrollHeight = document.documentElement.scrollHeight;
    const scrollTop = document.documentElement.scrollTop;
    const clientHeight = document.documentElement.clientHeight;

    if (scrollHeight - scrollTop === clientHeight && hasMore) {
      // User has scrolled to the bottom
      setVisibleItems((prevVisibleItems) => prevVisibleItems + 10); // Load more rows
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [handleScroll]);

  useEffect(() => {
    // Check if there are more rows to load
    setHasMore(visibleItems < data.length);
  }, [visibleItems, data]);

  return (
    <div>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            {/* Add other table headers as needed */}
          </tr>
        </thead>
        <tbody>
          {data.slice(0, visibleItems).map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              {/* Add other table cells as needed */}
            </tr>
          ))}
        </tbody>
      </Table>

      {hasMore && <p>Loading more...</p>}
    </div>
  );
};

export default InfiniteScrollTable;
