import './App.css';
import React, { useState } from 'react';
import ProgressBar from './components/ProgressBar/ProgressBar';
import PaginatedTable from './components/Table/PaginatedTable';
import InfiniteScrollTable from './components/Table/InfiniteScrollTable';
import { mockData } from './data'; // Adjust the path accordingly

function App() {
  const [progress, setProgress] = useState(80); // השתמש בסטייט כדי לעדכן את התקדמות הסרגל
  const itemsPerPage = 5; // Adjust the number of items per page as needed

  return (
    <div className="App">

      <header>
        <h1>UI - Components</h1>
      </header>

      <main>
        <PaginatedTable data={mockData} itemsPerPage={itemsPerPage} />
        <InfiniteScrollTable data={mockData} />
        <ProgressBar percentage={progress} />
      </main>
    </div>
  );
}

export default App;
